        Some(val) => println!("Got a value: &#123;&#125;", val),
        None => println!("No value found"),
    &#125;
    match option_from_err &#123;
        Some(val) => println!("Got a value: &#123;&#125;", val),
        None => println!("No value found"),
    &#125;
