enum ParseError &#123;
    Json(serde_json::Error),
    FieldMissing(&amp;'static str),
    TypeMismatch(&amp;'static str, &amp;'static str), // å­—æ®µå + æœŸæœ›ç±»å‹
    NumberRange(&amp;'static str), // æ•°å­—è¶…å‡ºèŒƒå›´
