// å¤„ç†ä»»åŠ¡
fn process_task(task_id: u32) &#123;
    println!("å¤„ç†ä»»åŠ¡&#123;&#125;...", task_id);
    thread::sleep(Duration::from_secs(1)); // æ¨¡æ‹Ÿè€—æ—¶1ç§’
    println!("ä»»åŠ¡&#123;&#125;å¤„ç†å®Œæˆ", task_id);
