    public static void rotateEdge(int[][] m, int a, int b, int c, int d) &#123;
        int tmp = 0;
        for (int i = 0; i &lt; d - b; i++) &#123;
            tmp = m[a][b + i];
            m[a][b + i] = m[c - i][b];
            m[c - i][b] = m[c][d - i];
            m[c][d - i] = m[a + i][d];
            m[a + i][d] = tmp;
        &#125;
