public int majorityElement(int[] nums) &#123;

        if(nums == null) &#123;
            return -1;
        &#125;
        int n = nums.length;
        int candi = 0;
        int HP = 0;
        for(int i  = 0; i &lt; n; i++) &#123;
            if(HP == 0) &#123;
                candi = nums[i];
                HP++;
            &#125;else if( nums[i] != candi) &#123;
                HP--;
            &#125;else &#123;
                HP++;
            &#125;
        &#125;
        if(HP == 0) &#123;
            return -1;
        &#125;
        HP = 0;
        for(int i = 0; i &lt; n ; i++) &#123;
            if(nums[i] == candi) &#123;
                HP++;
            &#125;
        &#125;
        if(HP > n / 2) &#123;
            return candi;
        &#125; else &#123;
            return -1;
        &#125;
    &#125;
