        dummyNode.next = head;

        //2 å…ˆæ‰¾åˆ°åè½¬éƒ¨åˆ†çš„leftå‰é¢pre
        ListNode pre = dummyNode;
        for(int i = 0; i &lt; left - 1;i++) &#123;
            pre = pre.next;
