    public static Node copyListWithRandom(Node head)&#123;
    	if(head == null)&#123; return null; &#125;
    	Node cur = head;
    	Node next = null;
    	// 1
    	while(cur != null)&#123;
    		next = cur.next;
    		cur.next = new Node(cur.value);
    		cur.next.next = next;
    		cur = next;
    	&#125;

    	cur = head;
    	Node curCopy = null;
    	// 2
    	while(cur != null)&#123;
    		next = cur.next.next;
    		curCopy = cur.next;
    		curCopy.rand = cur.rand != null ? cur.rand.next : null;
    		cur = next;
    	&#125;

    	cur = head;
    	Node res = head.next;
    	// 3
    	while(cur != null)&#123;
    		next = cur.next.next;
    		curCopy = cur.next;
    		curCopy.next = cur.next != null ? cur.next.next : null;
    		cur = next;
    	&#125;
    	return res;
	&#125;

    /**
