	public static int maxSubArray4(int[] nums)&#123;
		if (nums.length == 0 || nums == null) &#123;
			return 0;
		&#125;
        int max = nums[0];
        int pre = 0;
		for(int i = 0; i &lt; nums.length; i++)&#123;
			pre = Math.max(pre + nums[i], nums[i]);
			max = Math.max(pre, max);
        &#125;
		return max;
	&#125;
