public int uniquePaths(int m, int n) &#123;
        int[][] dp = new int[m][n];

        for(int i = 0; i &lt; m; i++) &#123;
            dp[i][0] = 1;

        &#125;
        for(int i = 0; i &lt; n; i++) &#123;
            dp[0][i] = 1;

        &#125;

        for(int i = 1; i &lt; m; i++) &#123;
            for(int j = 1; j &lt; n; j++) &#123;
                dp[i][j] = dp[i - 1][j]+ dp[i][j - 1];
            &#125;
        &#125;
        return dp[m-1][n-1];
