public int findLength(int[] A, int[] B) &#123;
		int n = A.length, m = B.length;
		int[][] dp = new int[n + 1][m + 1];
		int ans = 0;
		for (int i = n - 1; i >= 0; i--) &#123;
			for (int j = m - 1; j >= 0; j--) &#123;
				dp[i][j] = A[i] == B[j] ? dp[i + 1][j + 1] + 1 : 0;
				ans = Math.max(ans, dp[i][j]);
			&#125;
		&#125;
		return ans;
	&#125;

