	public static int process(int[] arr, int index, int rest) &#123;
		if (index == arr.length) &#123; // æ²¡é’±äº†
			return rest == 0 ? 1 : 0;
		&#125;
		int ways = 0;
		for (int zhang = 0; zhang * arr[index] &lt;= rest; zhang++) &#123; 
			ways += process(arr, index + 1, rest - (zhang * arr[index]));
		&#125;
		return ways;
	&#125;


