public int change(int amount, int[] coins) &#123;
        int[] dp = new int[amount + 1];
        dp[0] = 1;
        for (int coin : coins) &#123;
            for (int i = coin; i &lt;= amount; i++) &#123;
                dp[i] += dp[i - coin];
            &#125;
        &#125;
        return dp[amount];
