        public void insert(String word)&#123;
            if(word == null)&#123;
                return;
            &#125;
            char[] str = word.toCharArray();
            Node node = root;
            node.pass++;
            int path = 0;

            for(int i = 0; i &lt; str.length; i++)&#123;
                path = str[i] - 'a';
                if(node.nexts[path] == null)&#123;
                    node.nexts[path] = new Node();
                &#125;
                node = node.nexts[path];
                node.pass++;
            &#125;
            node.end++;
        &#125;

        /*
