        // 3 end --
        public void delete(String word)&#123;
            if(this.search(word) != 0)&#123;
                char[] str = word.toCharArray();
                Node node = root;
                node.pass--;
                int path = 0;
                for(int i = 0; i &lt; str.length; i++)&#123;
