public static int lengthOfLongestSubstring2(String s) &#123;
	if (s.length() == 0 || s == "") &#123;
		return 0;
	&#125;

	char[] str = s.toCharArray();
	int[] map = new int[256];
	for (int i = 0; i &lt; 256; i++) &#123;
		map[i] = -1;
	&#125;
	map[str[0]] = 0;
	int N = str.length;
	int ans = 1;
	int pre = 1;
	for (int i = 1; i &lt; N; i++) &#123;
		pre = Math.min(i - map[str[i]], pre + 1);
		ans = Math.max(ans, pre);
		map[str[i]] = i;	&#125;
	return ans;

