public static boolean isValidBST(TreeNode head)&#123;
		if(head != null)&#123;
			Stack&lt;TreeNode> stack = new Stack&lt;TreeNode>();
			double last = -Double.MAX_VALUE;// âš ï¸
			while(!stack.isEmpty() || head != null)&#123;
				// 1 
				if(head != null)&#123;
					stack.push(head);
					head = head.left;
				&#125;else&#123;// 2
					head = stack.pop();
					if(head.val &lt;= last)&#123;
						return false;
					&#125;
					last = head.val;
					head = head.right;
				&#125;
			&#125;
		&#125;
		return true;
	&#125;
