	public static class UnionFind2 &#123;
		private int[] parent;
		private int[] size;
		private int[] help;
		private int col;// ç”¨æ¥è®¡ç®—çŸ©é˜µæ•°r*col + c
		private int sets;

		public UnionFind2(char[][] board) &#123;
			col = board[0].length;
			sets = 0;
			int row = board.length;
			int len = row * col;
			parent = new int[len];
			size = new int[len];
			help = new int[len];
			for (int r = 0; r &lt; row; r++) &#123;
				for (int c = 0; c &lt; col; c++) &#123;
					if (board[r][c] == '1') &#123;
						int i = index(r, c);
						parent[i] = i;
						size[i] = 1;
						sets++;
					&#125;
				&#125;
			&#125;
		&#125;

		// (r,c) -> i
		private int index(int r, int c) &#123;
			return r * col + c;
		&#125;

		// åŸå§‹ä½ç½® -> ä¸‹æ ‡
		private int find(int i) &#123;
			int hi = 0;
			while (i != parent[i]) &#123;
				help[hi++] = i;
				i = parent[i];
			&#125;
			for (hi--; hi >= 0; hi--) &#123;
				parent[help[hi]] = i;
			&#125;
			return i;
		&#125;

		public void union(int r1, int c1, int r2, int c2) &#123;
			int i1 = index(r1, c1);
			int i2 = index(r2, c2);
			int f1 = find(i1);
			int f2 = find(i2);
			if (f1 != f2) &#123;
				if (size[f1] >= size[f2]) &#123;
					size[f1] += size[f2];
					parent[f2] = f1;
				&#125; else &#123;
					size[f2] += size[f1];
					parent[f1] = f2;
				&#125;
				sets--;
			&#125;
		&#125;

		public int sets() &#123;
			return sets;
		&#125;

	&#125;
