    // ä½¿ç”¨æ•°ç»„ç»“æ„çš„å¹¶æŸ¥é›†
    public static class UnionFind &#123;
        private int[] parents;
        private int[] size;
        private int[] help;
        private int sets;

        public UnionFind(int N) &#123;
            parents = new int[N];
            size = new int[N];
            help = new int[N];
            sets = N;

            for(int i = 0; i &lt; N; i++) &#123;
                parents[i] = i;
                size[i] = 1;
            &#125;
        &#125;

        // 1 å®šä¹‰ä¸€ä¸ªæŒ‡é’ˆ
        // 2 while(iä¸æ˜¯è‡ªå·±çˆ¹ï¼‰&#123;
        // 2.1 å‹helpæ ˆï¼ˆè™šæ‹Ÿçš„
        // 2.2 iæŒ‡å‘è‡ªå·±çš„çˆ¹    &#125;
        // 3 foréå†helpï¼šæ¨¡æ‹Ÿæ ˆå¼¹å‡º &#123;æ‰€æœ‰äººçˆ¹è®¾ä¸ºi&#125;
        // 4 è¿”å›i:æ­¤æ—¶iæŒ‡å‘æœ€çˆ¹
        public int findFather(int i) &#123;
            int hi = 0;
            while( i != parents[i]) &#123;
                help[hi++] = i;
                i = parents[i];
            &#125;
            for(hi--; hi >=0; hi--)&#123;
                parents[help[hi]] = i;
            &#125;
            return i;
        &#125;

        public void union(int i, int j) &#123;
            int iF = findFather(i);
            int jF = findFather(j);

            if(iF != jF) &#123;
                if(size[iF] &lt; size[jF]) &#123;
                    size[jF] += size[iF];
                    parents[iF] = jF;
                &#125;else &#123;
                    size[iF] += size[jF];
                    parents[jF] = iF;
                &#125;
                sets--;
            &#125;

        &#125;

        public int sets() &#123;
            return sets;
        &#125;

    &#125;


